INSERT INTO `article` VALUES ('4', '1', '2', '组织机构', '发规目督办', '发规目督办', '<p style=\"box-sizing: border-box; margin-top: 0px; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">发展规划与目标管理督查办公室下设综合科、发展规划科、目标督查科三个科室。</span></p><p><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\"><br/></span></p><p style=\"text-align: center;\"><img src=\"/assets/upload/ueditor/2017-06-20/166e3671559311e790d30050569a1c03.png\" title=\"image.png\" alt=\"image.png\"/></p>', '400', '2016-06-19 23:09:56', '2017-06-10 16:43:44', '1');
INSERT INTO `article` VALUES ('5', '1', '2', '机构职能', '发规目督办', '发规目督办', '<p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">发展规划与目标督查办公室的主要职能包括：对学校中长期发展战略规划及重要改革方案进行研究、编制、执行、监督及评估，对学校工作实施目标责任考核，跟踪、督查与反馈学校重大决策的实施、上级部门及学校重要文件精神、决定决议及重要事项贯彻落实情况等。下设机构有：综合管理科、发展规划科、目标督查科。</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">具体职责有：</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 黑体;\">（一）政策研究与决策咨询</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">1.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">收集、整理和分析国内外高等教育发展方面的信息，总结国内外大学办学规律与发展经验，为学校改革与发展提供决策咨询服务；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">2.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">围绕学校办学指标体系和宏观政策的制定进行调查研究，为学校领导全面了解学校运行情况以及科学决策提供相关分析报告；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">3.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">围绕学校发展目标与中心工作开展院校研究，提出有关工作思路、方案设计、政策措施等预见性参谋意见，为领导提供决策咨询服务；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">4.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">接受学校安排的其他政策研究工作。</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 黑体;\">（二）规划编制与实施</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">1.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">负责学校事业规划、中长期战略规划的研究、编制、执行、监督与评估的总体统筹工作；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">2.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">协调有关部门制定学科发展规划、师资队伍建设规划、校园建设规划等专项规划，协调全校各处、学院和直属单位制定发展规划的编制及实施；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">3.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">协调各处室、学院完成部门规划、专项规划以及学院规划的编制与实施，做好发展规划与年度计划的衔接工作；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">4.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">对学校发展规划与战略规划实施过程管理、考核评估和分析总结，提供规划实施和执行情况的年度报告、中期报告及总结报告；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">5.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">研究并协调发展规划实施过程中的有关重大问题。</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 黑体;\">（三）目标管理与考核</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">1.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">负责构建学校规划指标体系，制定学校目标管理考核办法；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">2.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">负责学校年度工作考核内容和目标任务，协调各部门、二级学院确定年度考核内容和目标任务，与各部门、院系签订目标责任书；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">3.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">依据目标责任书对各部门、学院的五年规划指标、年度计划指标的实现情况进行目标考核；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">4.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">将考核结果纳入相关部门干部年度考核指标体系，与相关部门共同提出考核等次初步意见与奖惩方案并上报学校党委常委会审定；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">5.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">研究解决考核工作中遇到的重要问题。</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 黑体;\">（四）工作督查与督办</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">1.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">督查督办教育厅等上级部门工作部署的贯彻落实情况；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">2.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">督查督办学校事业规划和年度工作要点的落实执行情况；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">3.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">督查督办学校党委常委会、校长办公会、学校专题工作会等决定的落实执行情况；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">4.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">督查督办校领导批（交）办事项的落实情况；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">5.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">督查督办信访事项及师生员工反映强烈的热点、难点问题；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">6.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">其它需要督查督办的事项。</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 黑体;\">（五）学校交办的其他工作。</span></p><p><br/></p>', '341', '2011-11-14 13:10:51', '2017-06-10 16:43:44', '1');
INSERT INTO `article` VALUES ('6', '1', '2', '岗位职责', '发规目督办', '发规目督办', '<p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-weight: 700;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 宋体;\">（<span style=\"color: rgb(51, 51, 51); font-family: 宋体; font-size: 19px; font-weight: bold; text-indent: 37px; background-color: rgb(255, 255, 255);\">一</span>）主任</span></span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">1.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">负责全面主持发展规划与目标管理督查办公室工作；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">2.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">负责批准办公室各方案的实施以及文件的签发；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">3.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">负责定期主持召开处务会并审定下阶段重点工作内容；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">4.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">负责主持发展规划委员会与目标管理督查委员会工作；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">5.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">完成学校领导交办的其他工作。</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-weight: 700;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 宋体;\">（二）副主任</span></span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">（分管发展规划科）</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">1.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">组织编制学校事业发展与改革规划和行动计划；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">2.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">及时了解国内外高校学科建设动态，收集国内外高校学科建设相关信息；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">3.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">跟踪国内外高校最新发展动态，结合学校实际定期编制《高校决策参考》；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">4.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">组织开展学校专题研究与院校研究，提供专题调研报告；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">5.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">完成上级领导交办的其他工作。</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-weight: 700;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 宋体;\">（三）副主任</span></span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">（分管目标督查科）</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">1.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">负责发展规划、年度计划执行情况的跟踪、反馈与协调；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">2.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">负责跟踪学校重要决策和工作部署的执行情况；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">3.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">负责了解上级部门工作部署的执行情况并及时反馈；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">4.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">负责编制《工作简报》，及时反映学校相关工作推进情况；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">5.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">完成上级领导交办的其他工作。</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">（四）综合科</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">1.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">负责办公室日常管理工作，包括档案管理、财务管理、信息管理、办公设备管理、报刊订阅与管理等工作；协助领导开展对外接待、会议安排等工作；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">2.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">负责公文起草、收发、登记、报批、存档；协调领导组织部门会议，并作好会议记录，撰写会议纪要；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">3.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">负责起草办公室年度工作计划、年度工作总结，协调处室其他部门完成日常管理工作；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">4.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">负责采集学校教育事业基础数据，对外发布学校相关统计数据报表，定期向学校领导提供数据分析报告;</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">5.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">完成上级领导交办的其他工作。</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">（五）发展规划科</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">1.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">协助领导做好学校发展规划、各职能部门、二级学院规划编制的资料搜集与汇编工作；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">2.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">具体负责搜集高教改革发展资讯信息以及《高校决策参考》的编制；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">3.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">参与开展院校研究。研究高等教育政策与法规，跟踪国内外高等教育发展趋势，围绕学校发展过程中重大问题与重大决策，开展院校研究和专题调研，为学校发展和科学决策提供研究报告、政策建议和方案设计;</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">4.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">完成领导安排的其他工作。</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">（六）目标督查科</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">1.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">协助领导制定与实施目标管理方案。参与研究制订发展规划实施的考核办法，确立中长期规划任期目标和与重点任务的分解方案，开展规划执行情况的年度考评、中期考评以及终期考评工作，适时反馈给各单位和相关主管部门和领导；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">2.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">协助领导完成上级部门工作部署落实情况的跟踪；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">3.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">协助领导完成学校事业规划、工作要点、重要决策和工作部署的执行情况的跟踪；</span></p><p style=\"box-sizing: border-box; margin-top: 1.6rem; margin-bottom: 1.6rem; color: rgb(51, 51, 51); font-family: &quot;Segoe UI&quot;, &quot;Lucida Grande&quot;, Helvetica, Arial, &quot;Microsoft YaHei&quot;, FreeSans, Arimo, &quot;Droid Sans&quot;, &quot;wenquanyi micro hei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, FontAwesome, sans-serif; white-space: normal; background-color: rgb(255, 255, 255); text-indent: 37px; line-height: 35px;\"><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">4.</span><span style=\"box-sizing: border-box; font-size: 19px; font-family: 仿宋;\">完成领导安排的其他工作。</span></p><p><br/></p>', '137', '2016-06-20 00:10:47', '2017-06-10 16:43:44', '1');
INSERT INTO `article` VALUES ('7', '1', '2', '工作人员', '发规目督办', '发规目督办', '<p style=\"text-align: center;\"><img src=\"/assets/upload/ueditor/2017-06-20/93659613559311e79e150050569a1c03.png\" title=\"image.png\" alt=\"image.png\"/></p>', '136', '2011-11-14 12:41:27', '2017-06-10 16:43:44', '1');
INSERT INTO `article` VALUES ('8', '1', '1', '关于开展2016年秋季开学工作专项检查的通知', '发展规划与目标管理督查办公室', 'xxx', '<p style=\"text-align: left;\"><span style=\"font-size:21px;font-family:仿宋\">各相关单位：</span></p><p style=\"text-indent: 43px\"><span style=\"font-size:21px;font-family:仿宋\">根据国务院教育督导委员会办公室《关于开展</span><span style=\"font-size:21px;font-family:仿宋\">2016 </span><span style=\"font-size:21px;font-family: 仿宋\">年秋季开学暨年度重点工作落实情况专项督导检查的通知》（国教督办函〔</span><span style=\"font-size:21px;font-family:仿宋\">2016</span><span style=\"font-size:21px;font-family: 仿宋\">〕</span><span style=\"font-size:21px;font-family:仿宋\">53 </span><span style=\"font-size:21px;font-family:仿宋\">号）和教育厅《关于开展</span><span style=\"font-size:21px;font-family:仿宋\">2016 </span><span style=\"font-size:21px;font-family: 仿宋\">年秋季开学工作专项检查的通知》（川教函〔</span><span style=\"font-size:21px;font-family:仿宋\">2016</span><span style=\"font-size:21px;font-family: 仿宋\">〕</span><span style=\"font-size:21px;font-family:仿宋\">435 </span><span style=\"font-size:21px;font-family: 仿宋\">号）要求,为保证学校开学各项工作落到实处，经研究，决定对开学工作和年度重点工作进行专项督导检查，请各机关单位高度重视，认真开展自查，形成自查报告，并于</span><span style=\"font-size:21px;font-family:仿宋\">9 </span><span style=\"font-size:21px;font-family:仿宋\">月</span><span style=\"font-size:21px;font-family:仿宋\">4 </span><span style=\"font-size:21px;font-family:仿宋\">日前将报告送交发展规划和目标管理督查办公室（办公楼</span><span style=\"font-size:21px;font-family:仿宋\">227 </span><span style=\"font-size:21px;font-family: 仿宋\">办公室），电子档发送至</span><span style=\"font-size:21px;font-family:仿宋\">445775093@qq.com </span><span style=\"font-size:21px;font-family:仿宋\">，联系人：王安源，联系电话：</span><span style=\"font-size:21px;font-family:仿宋\">2568114</span><span style=\"font-size:21px;font-family: 仿宋\">。</span></p><p style=\"text-indent: 43px\"><span style=\"font-size:21px;font-family:仿宋\">附件</span><span style=\"font-size:21px;font-family:仿宋\">:</span><span style=\"font-size:21px;font-family:仿宋\">西华师范大学</span><span style=\"font-size:21px;font-family:仿宋\">2016 </span><span style=\"font-size:21px;font-family:仿宋\">年秋季开学暨年度重点工作落实情况专项督导检查任务分解</span></p><p style=\"text-indent: 43px\"><span style=\"font-size:21px;font-family:仿宋\">&nbsp;</span></p><p style=\"margin-right: 43px; text-align: right;\"><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span><span style=\"font-size:21px;font-family:仿宋\">发展规划与目标管理督查办公室</span></p><p style=\"margin-right: 43px; text-align: right;\"><span style=\"font-size:21px;font-family:仿宋\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2016 </span><span style=\"font-size:21px;font-family:仿宋\">年</span><span style=\"font-size:21px;font-family:仿宋\">9 </span><span style=\"font-size:21px;font-family:仿宋\">月</span><span style=\"font-size:21px;font-family:仿宋\">1 </span><span style=\"font-size:21px;font-family:仿宋\">日</span></p><p style=\"line-height: 16px;\"><img style=\"vertical-align: middle; margin-right: 2px;\" src=\"http://dm.fgw.lintul.com/assets/ueditor/dialogs/attachment/fileTypeImages/icon_doc.gif\"/><a href=\"/uploadfile/2016-09-13/685c5642795511e6879200163e0003c5.docx\" target=\"_self\" title=\"1-附件.docx\" textvalue=\"附件.docx\">附件.docx</a></p><p><br/></p>', '165', '2016-09-13 09:50:19', '2017-06-20 16:07:51', '1');
INSERT INTO `article` VALUES ('9', '1', '1', '通知', '发展规划与目标管理督查办公室', 'xxx', '<p style=\"line-height:150%\"><span style=\"font-size:20px;line-height:150%;font-family:仿宋\">校属各单位：</span></p><p><span style=\"font-size:20px;font-family:仿宋\">&nbsp; &nbsp; 现将《发展规划与目</span><span style=\"font-size:20px;font-family:仿宋\">标管理督</span><span style=\"font-size:20px;font-family:仿宋\">查办公室工作职能（征求意见稿）》《西华师范大学发展规划与目标管理督查办公室2016年下半年工作要点（征求意见稿）》《发展规划与目标管理督查办公室2016年下半年月度工作计划（征求意见稿）》《学校目标管理考核工作流程（征求意见稿）》印发给各单位，请于9月19日前将反馈意见纸质版交送至发展规划与目标管理督查办公室（行政楼227），电子档发送至fgmdb@cwnu.edu.cn(联系人：刘婧，联系电话<span style=\"font-family: 仿宋; font-size: 20px;\">2568114)。</span></span></p><p><span style=\"font-family: 仿宋; font-size: 20px; line-height: 150%; text-indent: 40px;\">&nbsp; &nbsp; 附件1：《发展规划与目标管理督查办公室工作职能（征求意见稿）》</span><br/></p><p style=\"text-indent:40px;line-height:150%\"><span style=\"font-size:20px;line-height:150%;font-family: 仿宋\">附件2：《西华师范大学发展规划与目标管理督查办公室2016年下半年工作要点（征求意见稿）》</span></p><p style=\"text-indent:40px;line-height:150%\"><span style=\"font-size:20px;line-height:150%;font-family: 仿宋\">附件3：《发展规划与目标管理督查办公室2016年下半年月度工作计划（征求意见稿）》</span></p><p style=\"text-indent:40px;line-height:150%\"><span style=\"font-size:20px;line-height:150%;font-family: 仿宋\">附件4：《学校目标管理考核工作流程（征求意见稿）》</span></p><p style=\"text-indent:40px;line-height:150%\"><span style=\"font-family: 仿宋_GB2312; font-size: 20px; line-height: 30px; text-align: right;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 发展规划与目标管理督查办公室</span><br/></p><p style=\"white-space: normal;\"><span style=\"font-size: 20px; font-family: 仿宋_GB2312;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2016</span><span style=\"font-size: 20px; font-family: 仿宋_GB2312;\">年9月12日</span></p><p>&nbsp; &nbsp;&nbsp;<img src=\"http://dm.fgw.lintul.com/assets/uedito