INSERT INTO `article` VALUES ('103', '2', '2', '关于印发《西华师范大学发展规划委员会章程》的通知', '发展规划与目标管理督查办公室', '发展规划与目标管理督查办公室', '<p style=\"margin: 10px 0px; text-align: center; line-height: 32px;\"><span style=\"font-family: 黑体; font-size: 18px;\"></span></p><p style=\"margin-top:auto;margin-bottom: auto;text-align:center\"><span style=\"font-size: 36px;\"><strong><span style=\"font-family: 宋体; color: red; letter-spacing: -3px;\"></span></strong></span></p><p style=\"margin-top:auto;margin-bottom: auto;text-align:center\"><strong><span style=\"font-size:48px;font-family: 宋体;color:red;letter-spacing:-3px\"></span></strong></p><p style=\"margin-top:auto;margin-bottom: auto;text-align:center\"><strong><span style=\"font-size:48px;font-family: 华文宋体;color:red;letter-spacing:-3px\">中共西华师范大学委员会文件</span></strong></p><p style=\"margin-top:auto;margin-bottom: auto;text-align:center\"><br/></p><p style=\"margin-top:auto;margin-bottom: auto;text-align:center\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312, serif\"><br/></span></p><p style=\"margin-top: auto; margin-bottom: auto; text-align: center;\"><span style=\"font-size: 21px;font-family: 仿宋_GB2312, serif\">西华师大党委［2016］117号</span></p><p style=\"line-height: 19px\"><span style=\"font-size: 21px; font-family: 宋体; color: red; text-decoration: none;\">&nbsp;</span><span style=\"font-size: 21px; font-family: 宋体; color: red; text-decoration: underline;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"font-size:21px;font-family:宋体;color:red\">★<span style=\"text-decoration:underline;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></span><strong style=\"line-height: 35px; text-align: center;\"><span style=\"font-size: 24px;font-family: 方正小标宋简体;letter-spacing: -1px\">&nbsp;</span></strong></p><p style=\"line-height: 35px; text-align: center; background: white;\"><strong><span style=\"font-size: 24px;font-family: 方正小标宋简体;letter-spacing: -1px\">中共西华师范大学委员会关于印发</span></strong></p><p style=\"text-align:center;line-height:35px;background:white\"><strong><span style=\"font-size: 24px;font-family: 方正小标宋简体;letter-spacing: -1px\">《西华师范大学发展规划委员会章程》的通知</span></strong></p><p style=\"line-height: 35px; margin-top: 5px; background: white;\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 20px;\"><br/></span></p><p style=\"line-height: 35px; margin-top: 5px; background: white;\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 20px;\">校属各单位：</span><br/></p><p style=\"text-indent: 43px;line-height: 35px;background: white\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 20px;\">《西华师范大学发展规划委员会章程》经2016年第26次党委常委会审议通过，现印发给你们，请认真学习并遵照执行。</span></p><p style=\"text-indent: 43px;line-height: 35px;background: white\"><span style=\"font-size: 20px;\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 20px;\">附件：西华师范大学发展规划委员会章程</span><span style=\"font-family: 仿宋_GB2312, serif; font-size: 18px; line-height: 28px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></span></p><p style=\"line-height:28px\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 20px;\">&nbsp;</span></p><p style=\"text-indent:309px;line-height:28px\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 中共西华师范大学委员会</span></p><p style=\"margin-left:138px;text-indent:213px;line-height:28px\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 2016年12月23日</span></p><p style=\"margin-left:138px;text-indent:213px;line-height:28px\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 20px;\"><br/></span></p><p style=\"margin-left:138px;text-indent:213px;line-height:28px\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 20px; text-decoration: underline;\"></span></p><p style=\"line-height: 32px\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 19px; line-height: 27px; text-decoration: none;\">&nbsp;</span><span style=\"font-family: 仿宋_GB2312, serif; font-size: 19px; text-decoration: underline; line-height: 27px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></p><p style=\"line-height: 32px\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 19px; line-height: 27px; text-decoration: none;\">&nbsp;</span><span style=\"font-family: 仿宋_GB2312, serif; font-size: 19px; text-decoration: underline; line-height: 27px;\">&nbsp; &nbsp;&nbsp;<span style=\"font-family: 仿宋_GB2312, serif; text-decoration: underline; line-height: 27px; font-size: 16px;\">西华师范大学党委办公室 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2016年12月23日印 &nbsp; &nbsp;</span><span style=\"font-family: 仿宋_GB2312, serif; text-decoration: underline; line-height: 27px; font-size: 18px;\">&nbsp;</span></span></p><p style=\"margin: 10px 0px; text-align: left; line-height: 32px;\"><span style=\"font-family: 宋体; font-size: 21px;\"><br/></span></p><p style=\"margin: 10px 0px; text-align: left; line-height: 32px;\"><span style=\"font-size: 20px;\"><span style=\"font-family: 宋体;\">附件：</span><span style=\"font-family: 宋体;\">&nbsp;</span></span></p><p style=\"margin-top:8px;margin-right:0;margin-bottom:24px;margin-left:0;text-align:center;line-height:32px\"><strong><span style=\"font-size:24px;font-family:方正小标宋简体\">西华师范大学发展规划委员会章程</span></strong></p><p style=\"margin: 10px 0px; line-height: 32px; text-align: center;\"><span style=\"font-family: 黑体; font-size: 18px;\">第一章&nbsp; 总&nbsp; 则<br/></span></p><p style=\"text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第一条&nbsp; 为进一步加强学校改革与发展的科学化与民主化，推动教师民主参与决策咨询工作，促进学校事业健康快速发展，特设立西华师范大学发展规划委员会。</span></p><p style=\"margin-top: 10px;text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第二条&nbsp; 西华师范大学发展规划委员会（以下简称“发展规划委员会”）是学校发展规划工作的咨询、议事机构，受学校委托，参与编制与实施学校事业规划、中长期战略规划、专项规划、部门规划；研究学校规划实施过程中的重大问题并提出咨询意见和建议；研究对学校发展有重大影响的问题并提出咨询意见和建议。</span></p><p style=\"margin-top: 10px;text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第三条&nbsp; 发展规划委员会根据国家法律法规、学校规章制度及本章程开展工作。</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;text-align:center;line-height:32px\"><span style=\"font-family: 黑体; font-size: 18px;\">第二章&nbsp; 机构设置</span></p><p style=\"margin-top: 10px;text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第四条&nbsp; 发展规划委员会委员由学校教职工担任，也可聘请国内外相关领域权威专家为委员会正式委员，委员人数为20-35人，委员会设主席、副主席若干名。</span></p><p style=\"margin-top: 10px;text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第五条&nbsp; 发展委员会下设秘书处，由发展规划与目标管理督查办公室承担秘书处工作。</span></p><p style=\"margin-top: 10px;text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第六条&nbsp; 发展规划委员会可下设若干发展规划专项工作小组，开展专题调查研究。</span></p><p style=\"margin-top: 10px;text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第七条&nbsp; 发展规划委员会工作经费以专项经费形式纳入学校预算。</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;text-align:center;line-height:32px\"><span style=\"font-family: 黑体; font-size: 18px;\">第三章&nbsp; 机构职能</span></p><p style=\"text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第八条&nbsp; 发展规划委员会对学校事业规划、中长期战略规划、专项规划、部门规划的编制与实施具有研究、建议的职能，具体包括：</span></p><p style=\"text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（一）研究学校各项发展规划起草的实施办法、总体框架、重点内容。</span></p><p style=\"text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（二）草拟学校事业规划、中长期战略规划、专项规划、部门规划等草案。</span></p><p style=\"text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（三）对学校规划实施过程中的重大问题和对学校发展有重大影响的问题，提出咨询意见和建议。</span></p><p style=\"text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（四）论证二级学院、研究机构等的增减设置问题。</span></p><p style=\"text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（五）讨论学校各项发展规划的实施细则和目标任务分解。</span></p><p style=\"text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（六）定期调研学校各项规划的实施进展情况，进行中期检查与终期评估并形成书面报告。</span></p><p style=\"text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（七）制定、修改发展规划委员会章程。</span></p><p style=\"text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（八）研究党委常委会、校长办公会委托的其他相关重要事项。</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;text-align:center;line-height:32px\"><span style=\"font-family: 黑体; font-size: 18px;\">第四章&nbsp; 工作程序</span></p><p style=\"text-indent:43px;line-height:32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第九条&nbsp; 发展规划委员会议题经委员会主席签字后以书面方式提出。议题确立后，报秘书处。</span></p><p style=\"margin-top: 10px;text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第十条&nbsp; 会议由委员会主席（或由主席委托的副主席）负责召集。根据需要不定期召开全体会议、专题会议或部分委员会议。</span></p><p style=\"margin-top: 10px;text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第十一条&nbsp; 可视情况邀请相关机关部门、教辅单位和二级学院负责人与师生员工代表参会。</span></p><p style=\"margin-top: 10px;text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第十二条&nbsp; 发展规划委员会的意见、建议、报告等，经校长办公会讨论后提交常委会讨论决定。</span></p><p style=\"margin-top: 10px;text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第十三条&nbsp; 会务工作由秘书处承担。秘书处应做好会议记录，形成会议纪要并及时印发给主席、副主席和委员。</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;text-align:center;line-height:32px\"><span style=\"font-family: 黑体; font-size: 18px;\">第五章&nbsp; 委员权责</span></p><p style=\"text-indent: 37px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第十四条&nbsp; 发展规划委员会委员由学校正式聘任。</span></p><p style=\"margin-top: 10px;text-indent: 37px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第十五条&nbsp; 发展规划委员会委员权利:</span></p><p style=\"text-indent: 37px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（一）参加发展规划委员会全体会议、专项会议、部分会议。</span></p><p style=\"text-indent: 37px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（二）可获取发展规划委员会开展工作的相关资料。</span></p><p style=\"text-indent: 37px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（三）开展发展规划委员会委托的调研工作，相关费用从发展规划委员会专项经费中支出。</span></p><p style=\"margin-top: 10px;text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第十六条&nbsp; 发展规划委员会委员职责：</span></p><p style=\"text-indent: 37px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（一）提升自身专业知识水平，不断增强履职能力。</span></p><p style=\"text-indent: 37px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（二）积极建言献策，为学校发展规划的制定与实施提供客观、公正、充分、明确的意见和建议。</span></p><p style=\"text-indent: 37px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（三）发展规划委员会会议资料未经允许不得擅自公开。</span></p><p style=\"margin-top:10px;margin-right:0;margin-bottom:10px;margin-left:0;text-align:center;line-height:32px\"><span style=\"font-family: 黑体; font-size: 18px;\">第六章&nbsp; 附&nbsp; 则</span></p><p style=\"text-indent: 43px;line-height: 32px\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">第十七条&nbsp; 本章程由发展规划与目标管理督查办公室负责解释。</span></p><p style=\"margin-top: 10px;text-indent: 43px;line-height: 32px\"><span style=\"font-size: 18px;\"><span style=\"font-family: 仿宋_GB2312;\">第十八条&nbsp; 本章程经学校党委常委会批准后生效。</span><span style=\"font-family: 仿宋_GB2312; text-align: center;\">&nbsp;</span></span></p><p><br/></p>', '152', '2016-12-28 09:34:45', '2017-06-20 16:07:52', '1');
INSERT INTO `article` VALUES ('104', '2', '1', '关于印发《西华师范大学督查督办工作办法（试行）》的通知', '发展规划与目标管理督查办公室', '发展规划与目标管理督查办公室', '<p style=\"margin-bottom:8px;text-indent:43px;line-height:33px\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 仿宋_GB2312;\"></span></strong></span></p><p style=\"margin-top: auto; margin-bottom: auto; white-space: normal; text-align: center;\"><strong><span style=\"font-size: 48px; font-family: 华文宋体; color: red; letter-spacing: -3px;\">中共西华师范大学委员会文件</span></strong></p><p style=\"margin-top: auto; margin-bottom: auto; white-space: normal; text-align: center;\"><br/></p><p style=\"margin-top: auto; margin-bottom: auto; white-space: normal; text-align: center;\"><span style=\"font-size: 21px; font-family: 仿宋_GB2312, serif;\"><br/></span></p><p style=\"margin-top: auto; margin-bottom: auto; white-space: normal; text-align: center;\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 20px;\">西华师大党委［2016］116号</span></p><p style=\"white-space: normal; line-height: 19px;\"><span style=\"font-size: 21px; font-family: 宋体; color: red;\">&nbsp;</span><span style=\"font-size: 21px; font-family: 宋体; color: red; text-decoration: underline;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span><span style=\"font-size: 21px; font-family: 宋体; color: red;\">★<span style=\"text-decoration: underline;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></span><strong style=\"line-height: 35px; text-align: center;\"><span style=\"font-size: 24px; font-family: 方正小标宋简体; letter-spacing: -1px;\">&nbsp;</span></strong></p><p style=\"white-space: normal; line-height: 35px; text-align: center; background: white;\"><strong><span style=\"font-size: 24px; font-family: 方正小标宋简体; letter-spacing: -1px;\"></span></strong></p><p style=\"text-align:center;line-height:32px;background:white\"><strong><span style=\"font-size: 24px;font-family: 方正小标宋简体;letter-spacing: -1px\">中共西华师范大学委员会关于印发</span></strong></p><p style=\"text-align:center;line-height:32px;background:white\"><strong><span style=\"font-size: 24px;font-family: 方正小标宋简体;letter-spacing: -1px\">《西华师范大学督查督办工作办法（试行）》的通知</span></strong></p><p style=\"margin-bottom: 8px;line-height: 32px;background: white\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 18px;\"><br/></span></p><p style=\"margin-bottom: 8px;line-height: 32px;background: white\"><span style=\"font-size: 20px; font-family: 仿宋_GB2312, serif;\">校属各单位：<br/></span></p><p style=\"margin-bottom: 8px;text-indent: 43px;line-height: 32px;background: white\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 20px;\">《西华师范大学督查督办工作办法（试行）》经2016年第26次党委常委会审议通过，现印发给你们，请认真学习并遵照执行。</span></p><p style=\"margin-bottom: 8px;text-indent: 43px;line-height: 32px;background: white\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 20px;\">附件：西华师范大学督查督办工作办法（试行）</span></p><p style=\"line-height:28px\"><span style=\"font-size: 20px;\"><span style=\"font-size: 21px; font-family: 仿宋_GB2312, serif;\">&nbsp;</span><span style=\"font-family: 仿宋_GB2312, serif; font-size: 21px;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</span></span></p><p style=\"text-indent:309px;line-height:28px\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;中共西华师范大学委员会</span></p><p style=\"margin-left:138px;text-indent:213px;line-height:28px\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 20px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2016年12月23日</span></p><p style=\"margin-left:138px;text-indent:213px;line-height:28px\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 20px;\"><br/></span></p><p style=\"white-space: normal; line-height: 35px; text-align: center; background: white;\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 19px; line-height: 27px; text-decoration: underline;\"></span></p><p style=\"white-space: normal; line-height: 32px;\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 19px; line-height: 27px; text-decoration: none;\">&nbsp;</span><span style=\"font-family: 仿宋_GB2312, serif; font-size: 19px; text-decoration: underline; line-height: 27px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span></p><p style=\"white-space: normal; line-height: 32px;\"><span style=\"font-family: 仿宋_GB2312, serif; font-size: 19px; line-height: 27px;\">&nbsp;</span><span style=\"font-family: 仿宋_GB2312, serif; font-size: 19px; text-decoration: underline; line-height: 27px;\">&nbsp; &nbsp;&nbsp;<span style=\"font-size: 16px;\">西华师范大学党委办公室 &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;2016年12月23日印 &nbsp; &nbsp;</span><span style=\"font-size: 18px;\">&nbsp;</span></span></p><p style=\"white-space: normal; line-height: 35px; text-align: left; background: white;\"><span style=\"font-family: 宋体; font-size: 18px; line-height: 31px; text-indent: 43px;\"><br/></span></p><p style=\"white-space: normal; line-height: 35px; text-align: left; background: white;\"><span style=\"font-family: 宋体; line-height: 31px; text-indent: 43px; font-size: 20px;\">附件：</span><br/></p><p style=\"line-height: 31px; text-align: center; margin-top: 5px; margin-bottom: 5px;\"><strong><span style=\"font-size:24px;font-family: 方正小标宋简体\">西华师范大学督查督办工作办法（试行）</span></strong><br/></p><p style=\"margin-bottom: 8px; text-indent: 43px; text-align: justify; line-height: 1.5em; margin-top: 5px;\"><strong style=\"font-size: 18px;\"><span style=\"font-family: 仿宋_GB2312;\"><br/></span></strong></p><p style=\"margin-bottom: 8px; text-indent: 43px; text-align: justify; line-height: 1.5em; margin-top: 5px;\"><strong style=\"font-size: 18px; line-height: 33px;\"><span style=\"font-family: 仿宋_GB2312;\">第一条</span></strong><span style=\"font-size: 18px; line-height: 33px; font-family: 仿宋_GB2312;\">&nbsp; 为进一步提升管理水平，提高工作效率，加强执行能力建设，切实推动学校重大决策、重要工作部署的贯彻落实和各项工作的有效开展，根据《中共中央办公厅关于加强和改进党委督促检查工作的意见》（中办发〔2012〕17号）、《国务院办公厅关于进一步加强政府督促检查工作的意见》（国办发〔2014〕42号）等精神，结合学校实际，制定本办法。</span><br/></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 仿宋_GB2312;\">第二条</span></strong><span style=\"font-family: 仿宋_GB2312;\">&nbsp; 督查督办工作的主要内容</span></span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（一）教育厅等上级部门工作部署的贯彻落实情况；</span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（二）学校发展规划和年度工作要点的落实情况；</span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（三）学校党委常委会、校长办公会、学校专题工作会等会议决议、决定的执行情况；</span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（四）校领导批（交）办事项的落实情况；</span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（五）各单位目标责任与工作任务的完成情况；</span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（六）信访事项及师生员工反映强烈的热点、难点问题；</span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（七）其它需要督查督办的事项。</span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 18px; font-family: 仿宋_GB2312;\">第三条&nbsp; </span></strong><span style=\"font-size: 18px; font-family: 仿宋_GB2312;\">学校成立督查督办工作领导小组，负责督查督办工作的统筹协调、整体推进、督促落实。学校党政主要领导担任领导小组组长，其他校领导及相关部门负责人担任领导小组成员。</span></span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">领导小组下设督查督办工作办公室，挂靠在发展规划与目标管理督查办公室，具体<span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">负责领导小组安排的工作任务，组织实施学校督查督办工作。</span></span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 仿宋_GB2312;\">第四条</span></strong><span style=\"font-family: 仿宋_GB2312;\">&nbsp; 督查督办工作的基本原则</span></span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 31px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（一）实事求是。从实际出发，求真务实，全面客观，杜绝弄虚作假，如实反馈情况。</span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 31px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（二）突出重点。服务学校发展大局，紧密围绕学校中心工作，着力抓好学校建设和发展中的重大决策、重要部署和重点工作的贯彻落实。</span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 31px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（三）务求实效。以抓落实为出发点，准确把握督查督办工作要求，及时客观反映情况，切实做到准确、实效。</span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-size: 18px; font-family: 仿宋_GB2312;\">第五条&nbsp; </span></strong><span style=\"font-size: 18px; font-family: 仿宋_GB2312;\">督查督办的工作方式</span></span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（一）定期督查。对常规工作采取现场督查、实地调研、查阅资料等方式跟踪督查，及时掌握工作进度。</span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（二）专项督查。对上级或学校明确要求限期完成的重要专项工作，以及各种会议决议、决定和领导批示、交办的工作，视任务要求进行专项督查。</span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（三）联合督查。对涉及面广、持续时间长和影响重大的综合性工作，由督查督办工作办公室牵头，相关部门参与进行联合督查。</span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 仿宋_GB2312;\">第六条</span></strong><span style=\"font-family: 仿宋_GB2312;\">&nbsp; 督查督办的工作程序</span></span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（一）督查立项。对需立项督查的事项，督查督办工作办公室根据工作职责分工和督查工作要求，提出立项意见，报领导小组审定后立项。立项内容包括：督办事项、承办单位、办理时限和办理要求等。</span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（二）督查通知。督查事项立项后，督查督办工作办公室向承办单位发送《西华师范大学督查督办通知单》。对各种会议已明确承办单位和办理时限的事项以及领导批示和交办等无需立项的事项，督查督办工作办公室不再另发通知。 </span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（三）信息反馈。承办单位收到督查督办通知单后，应按照要求将办理结果在规定时限内反馈至督查督办工作办公室。督查督办工作办公室编印《西华师范大学督查督办通报》，对督查督办事项的进展情况进行全校通报。</span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-family: 仿宋_GB2312; font-size: 18px;\">（四）立卷归档。督查事项办结后，督查督办工作办公室及时将相关材料进行整理归档、妥善保管。</span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 仿宋_GB2312;\">第七条&nbsp; </span></strong><span style=\"font-family: 仿宋_GB2312;\">学校将督查督办结果作为重要指标列入各单位目标管理考核指标体系，并作为干部和教职工年度考核、评优、评奖等的参考依据。</span></span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 仿宋_GB2312;\">第八条&nbsp; </span></strong><span style=\"font-family: 仿宋_GB2312;\">本办法由发展规划与目标管理督查办公室负责解释。</span></span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-size: 18px;\"><strong><span style=\"font-family: 仿宋_GB2312;\">第九条&nbsp; </span></strong><span style=\"font-family: 仿宋_GB2312;\">本办法自2017年1月1日起执行。</span></span></p><p style=\"margin-bottom: 8px; text-indent: 43px; line-height: 33px; text-align: justify;\"><span style=\"font-size: 18px; font-family: 仿宋_GB2312;\"><br/></span></p><p><br/></p>', '154', '2016-12-28 09:06:07', '2017-06-20 16:07:52', '1');
INSERT INTO `article` VALUES ('105', '2', '1', '四川省高校第四期优秀中青年干部递进培训班学员来我校调研', '宣传部', '发规目督办', '<p style=\";margin-bottom:0;text-indent:32px;line-height:35px\"><span style=\"font-size: 20px; font-family: 仿宋;\">2017年1月4日上午，四川省高校第四期优秀中青年干部递进培训班29名学员来我校调研。校党委副书记聂应德接待了来访学员，在办公楼六楼会议室举行了座谈。我办主任周勇和我校其他主要部门负责同志参加座谈。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:35px\"><span style=\"font-family: 仿宋; font-size: 20px;\">聂应德介绍了我校的办学历史、学科建设特色、建校70年来取得的办学成就以及下一步在人才培养、学科建设、科学研究、办学特色、社会服务等方面的努力方向和建设目标。他说培训班29名同仁此次到我校调研交流，既是对我校工作的认可，也是我校学习兄弟院校先进经验、提升工作水平的好机会，希望各兄弟院校此行介绍好的办学经验、办学思路及做法，推动双方事业共同发展。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:35px\"><span style=\"font-family: 仿宋; font-size: 20px;\">我办主任周勇就我校推进学校综合改革工作的措施、承担的两项省级教育综合改革试点项目的执行情况作了详细介绍。针对人事编制、招生制度改革、拨款方式改革、办学自主权、非中心城市办学的人才稳定和政策倾斜、目标管理和绩效考核等六个高校热点、难点问题的改革进行了深入探讨。</span></p><p style=\";margin-bottom:0;text-indent:32px;line-height:35px\"><span style=\"font-family: 仿宋; font-size: 20px;\">教务处、审计处负责同志介绍了我校人才培养、创新创业、审计等方面的情况。</span></p><p style=\"text-align: center;\"><span style=\"font-family: 仿宋; font-size: 20px;\"><img src=\"/uploadfile/2017-01-06/17917398d3b511e691c70050569a1c03.bmp\" title=\"1.bmp\" alt=\"1.bmp\" width=\"628\" height=\"361\" style=\"width: 628px; height: 361px;\"/></span></p><p style=\"text-align: center;\"><span style=\"font-family: 仿宋; font-size: 20px;\"><img src=\"/uploadfile/2017-01-06/3a2cea37d3b511e691c70050569a1c03.bmp\" title=\"2.bmp\" alt=\"2.bmp\" width=\"627\" height=\"351\" style=\"width: 627px; height: 351px;\"/></span></p><p style=\"text-align: center;\"><img src=\"/uploadfile/2017-01-06/5728fd08d3b511e691c70050569a1c03.bmp\" title=\"3.bmp\" alt=\"3.bmp\" width=\"631\" height=\"386\" style=\"width: 631px; height: 386px;\"/></p>', '195', '2017-01-06 10:03:03', '2017-06-20 16:07:52', '1');
INSERT INTO `article` VALUES ('106', '2', '1', '2017年发展规划与目标管理督查办公室寒假春节值班安排', '发展规划与目标管理督查办公室', '发展规划与目标管理督查办公室', '<p style=\"margin-top:auto;margin-bottom: auto;text-align:center\"><br/></p><table cellspacing=\"0\" cellpadding=\"0\"><tbody><tr style=\";height:52px\" class=\"firstRow\"><td width=\"155\" style=\"border-color: windowtext; border-width: 1px; padding: 0px 7px;\" height=\"52\"><p style=\"margin-top:auto;margin-bottom:   auto;text-align:center\"><strong><span style=\"font-size:20px;font-family:仿宋_GB2312\">值班时间</span></strong></p></td><td width=\"139\" style=\"border-top-color: windowtext; border-right-color: windowtext; border-bottom-color: windowtext; border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px; border-left-style: none; padding: 0px 7px;\" height=\"52\"><p style=\"margin-top:auto;margin-bottom:   auto;text-align:center\"><strong><span style=\"font-size:20px;font-family:仿宋_GB2312\">值班领导</span></strong></p></td><td width=\"144\" style=\"border-top-color: windowtext; border-right-color: windowtext; border-bottom-color: windowtext; border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px; border-left-style: none; padding: 0px 7px;\" height=\"52\"><p style=\"margin-top:auto;margin-bottom:   auto;text-align:center\"><strong><span style=\"font-size:20px;font-family:仿宋_GB2312\">办公电话</span></strong></p></td><td width=\"161\" style=\"border-top-color: windowtext; border-right-color: windowtext; border-bottom-color: windowtext; border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px; border-left-style: none; padding: 0px 7px;\" height=\"52\"><p style=\"margin-top:auto;margin-bottom:   auto;text-align:center\"><strong><span style=\"font-size:20px;font-family:仿宋_GB2312\">联系电话</span></strong></p></td><td width=\"123\" style=\"border-top-color: windowtext; border-right-color: windowtext; border-bottom-color: windowtext; border-top-width: 1px; border-right-width: 1px; border-bottom-width: 1px; border-left-sty